import React, { useState } from "react";

function ListTasks({ tasks, removeTask }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(5); // Change this value as needed
  const [newTitle, setNewTitle] = useState("");
  const [completed, setCompleted] = useState(false);

  const indexOfLastTask = currentPage * itemsPerPage;
  const indexOfFirstTask = indexOfLastTask - itemsPerPage;
  const currentTasks = tasks.slice(indexOfFirstTask, indexOfLastTask);

  const totalPages = Math.ceil(tasks.length / itemsPerPage);

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const handleCheckboxChange = (event) => {
    setCompleted(event.target.checked);
  };

  const handleTitleChange = (event) => {
    setNewTitle(event.target.value);
  };

  const handleEditSubmit = (event, task) => {
    event.preventDefault();
    task.title = newTitle;
    setNewTitle("");
  };

  const handleEditCancel = () => {
    setNewTitle("");
  };

  return (
    <div>
      {currentTasks.map((task, index) => (
        <div key={index} className={`list-task ${task.completed ? "completed" : ""}`}>
          {task.editing ? (
            <form onSubmit={(e) => handleEditSubmit(e, task)} className="form">
              <input
                type="text"
                value={newTitle}
                onChange={handleTitleChange}
                placeholder="Edit task"
              />
              <button type="submit">Save</button>
              <button type="button" onClick={handleEditCancel}>
                Cancel
              </button>
            </form>
          ) : (
            <>
              {task.title}
              <button className="edit-btn" onClick={() => task.editing = true}>
                Edit
              </button>
            </>
          )}
          <input
            className="checkbox"
            type="checkbox"
            checked={completed}
            onChange={handleCheckboxChange}
          />
          <button onClick={() => removeTask(index)} className="delete-btn">
            Delete
          </button>
        </div>
      ))}

      <div className="pagination">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        <span>Page {currentPage} of {totalPages}</span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default ListTasks;
